<?php if (!defined('THINK_PATH')) exit();?><style type="text/css">    .index_order_pay{        font-size: 1rem;        color: #999999;    }    .index_order_pay span{        float: right;        padding: 0px 30px 0px 0px;        color: #333333;    }    .index_pay_title{        font-size: 1.2rem;        padding: 1rem;        text-align: center;    }    .index_pay_title .close{        float: left;        width: 2rem;        height: 2rem;        display: block;        font-size: 1.5rem;        color: #999999;        margin: -5px 0px 0px -10px;    }</style><div class="index_pay_title">    <span class="close icon-func" onclick="javascript:layer.closeAll();">x</span>    付款详情</div><ul class="mui-table-view index_order_pay">    <li class="mui-table-view-cell">        <a class="mui-navigate-right noloading">            <span><?php echo ($order["phone"]); ?></span>            手机号码        </a>    </li>    <li class="mui-table-view-cell">        <a class="mui-navigate-right noloading">            <span>全国流量直充 <?php echo ($order["package_name"]); ?></span>            套餐名称        </a>    </li>    <li class="mui-table-view-cell">        <a class="mui-navigate-right noloading">            <span>¥ <?php echo ($order["order_money"]); ?> 元</span>            订单金额        </a>    </li>    <ul class="mui-table-view mui-table-view-radio">        <?php if(($is_wechat) == "1"): ?><li class="mui-table-view-cell <?php if(($defaultpay) == "wechatpay"): ?>mui-selected<?php endif; ?>" onclick="$('#pay_code').val('wechatpay');">                <a class="mui-navigate-right noloading" id="chooseWXPay">                    <i class="user-icon-weixin pay-type-weixin"></i> 微信支付                </a>            </li><?php endif; ?>        <li class="mui-table-view-cell <?php if(($defaultpay) == "linqian"): ?>mui-selected<?php endif; ?>" onclick="$('#pay_code').val('linqian');">            <a class="mui-navigate-right noloading">                <div class="section-box">                    <div class="section-box-item"><i class="user-icon-iconhqb01 pay-type-linqian"></i> 零钱支付</div>                    <div class="section-box-item linqian-balance">余额：<?php echo ((isset($user_money) && ($user_money !== ""))?($user_money):"0.00"); ?>元</div>                </div>            </a>        </li>    </ul>    <li class="mui-table-view-cell">        <input type="hidden" id="pay_code" value="<?php echo ($defaultpay); ?>">        <input type="hidden" id="order_money" value="<?php echo ((isset($order["order_money"]) && ($order["order_money"] !== ""))?($order["order_money"]):'0.00'); ?>">        <input type="hidden" id="amount" value="<?php echo ((isset($user_money) && ($user_money !== ""))?($user_money):'0.00'); ?>">        <input type="hidden" id="order_id" value="<?php echo ($order["order_id"]); ?>">        <button type="button" class="mui-btn-block mui-btn-mini mui-btn-success btn-block" onclick="pay_check();">立即支付</button>    </li></ul><script type="text/javascript">    function pay_check(){        var pay_code = $("#pay_code").val();        if(pay_code == 'linqian'){            amount();        }else if(pay_code == 'wechatpay'){            callpay();        }    }    function amount(){        var order_money = document.getElementById('order_money').value;        var amount = document.getElementById('amount').value;        var order_id = document.getElementById('order_id').value;        if(parseFloat(order_money) > parseFloat(amount)){            alert("很抱歉，您的余额不足！！");        }    }</script><script type="text/javascript">    <?php if(($is_wechat) == "1"): ?>//调用微信JS api 支付    function jsApiCall(){        WeixinJSBridge.invoke(            'getBrandWCPayRequest',            <?php echo ($jsApiParameters); ?>,            function(res){                WeixinJSBridge.log(res.err_msg);                if(res.err_msg == 'get_brand_wcpay_request:cancel') {                    window.location.href="/index.php/user/order_detail/id/<?php echo ($last_id); ?>";                    alert("您已取消了此次支付");                    return;                } else if(res.err_msg == 'get_brand_wcpay_request:fail') {                    window.location.href="/index.php/user/order_detail/id/<?php echo ($last_id); ?>";                    alert("支付失败");                    return;                } else if(res.err_msg == 'get_brand_wcpay_request:ok') {                    window.location.href="/index.php/user/orders";                    //alert("支付成功！");//跳转到订单页面                                    } else {                    alert("未知错误"+res.error_msg);                    return;                }            }            );    }    function callpay(){        if (typeof WeixinJSBridge == "undefined"){            if( document.addEventListener ){                document.addEventListener('WeixinJSBridgeReady', jsApiCall, false);            }else if (document.attachEvent){                document.attachEvent('WeixinJSBridgeReady', jsApiCall);                 document.attachEvent('onWeixinJSBridgeReady', jsApiCall);            }        }else{            jsApiCall();        }    }<?php endif; ?></script>